mapper.update:

update board
   set hit = hit + 1
 where no = #{no }


update board 
   set title=#{title },
       content=#{content }
 where no=#{no }
   and member_no=#{userNo }


update board
   set o_no = o_no + 1
 where g_no = #{groupNo }
   and o_no > #{orderNo }

mapper.delete:

delete
  from board
 where no = #{no }
   and member_no = #{userNo }
